import React from 'react'
import DefaultHeader from './../Header/header'
import HistoryPage from './History/history';
import {Link,BrowserRouter,Route} from 'react-router-dom'
import SalePage from './Sale/sale';
import { Button } from 'antd';
var HomePageCSS= require('./homepage.css')



var x=0
function a(){
    x=1;
}

function b(){
    x=2;
}

function c(){
    x=3;
}

function d(){
    x=4;
}
function Change(){
    document.getElementById("F").classList.remove("block");
    document.getElementById("G").classList.remove("block");
    document.getElementById("H").classList.remove("block");
    document.getElementById("I").classList.remove("block");
    document.getElementById("A").classList.remove("block");
    document.getElementById("B").classList.remove("block");
    document.getElementById("C").classList.remove("block");
    document.getElementById("D").classList.remove("block");
    if(x==1){
        document.getElementById("F").classList.add("block");
        document.getElementById("A").classList.add("block");
    }else if(x==2){
        document.getElementById("G").classList.add("block");
        document.getElementById("B").classList.add("block");
    }else if(x==3){
        document.getElementById("H").classList.add("block");
        document.getElementById("C").classList.add("block");
    }else if(x==4){
        document.getElementById("I").classList.add("block");
        document.getElementById("D").classList.add("block");
    }
}

export default class HomePage extends React.Component{
    render(){
        return(
            <div className={HomePageCSS.homePage}>
            <DefaultHeader/>
            <div className={HomePageCSS.type}>
            <div>
                    <dl className={HomePageCSS.dl1}><dt className={HomePageCSS.dt1}>类型:</dt>
                    <dd className={HomePageCSS.dd1}>
                        <a><Link to="/act/ry">动作</Link></a>
                        <a><Link to="/comedy/ry">喜剧</Link></a>
                        <a><Link to="/love/ry">爱情</Link></a>
                        <a><Link to="/act">恐怖</Link></a>
                        <a><Link to="/act">科幻</Link></a>
                        <a><Link to="/act">剧情</Link></a>
                        <a><Link to="/act">犯罪</Link></a>
                        <a><Link to="/act">惊悚</Link></a>
                        <a><Link to="/act">悬疑</Link></a>
                        <a><Link to="/act">文艺</Link></a>
                        <a><Link to="/act">战争</Link></a>
                        <a><Link to="/act">伦理</Link></a>
                        </dd>
                        </dl>
                        </div>
                        <div>
                    <dl className={HomePageCSS.dl2}><dt className={HomePageCSS.dt2}>地区:</dt>
                    <dd className={HomePageCSS.dd2}>
                        <a><Link to="/act/hp">美国</Link></a>
                        <a><Link to="/comedy/hp">大陆</Link></a>
                        <a><Link to="/love/hp">香港</Link></a>
                        <a><Link to="/act">韩国</Link></a>
                        <a><Link to="/act">日本</Link></a>
                        <a><Link to="/act">法国</Link></a>
                        <a><Link to="/act">英国</Link></a>
                        <a><Link to="/act">台湾</Link></a>
                        <a><Link to="/act">泰国</Link></a>
                        <a><Link to="/act">印度</Link></a>
                        <a><Link to="/act">其他</Link></a>
                    </dd></dl></div>
                    </div>
                {/* <BrowserRouter basename="homepage"> */}
                <div className={HomePageCSS.list}>
                    电影排行榜
                <div className={HomePageCSS.list1}>
                <Link to="/homepage/history"><Button shape="round" className={HomePageCSS.l1}>热播榜</Button></Link>
                <Link to="/homepage/sale"><Button shape="round" className={HomePageCSS.l2}>动作榜</Button></Link>
                </div></div>
                <div>
                    高分电影榜
                </div>







               <div>
               <Route path="/homepage/history" component={HistoryPage}></Route>
                <Route path="/homepage/sale" component={SalePage}></Route>
               </div>
                {/* </BrowserRouter> */}
            </div>
        )
    }
}
