import React from 'react'
import DefaultHeader from './../Header/header'
import { Form, Icon, Input, Button, Checkbox } from 'antd';
import {Link,BrowserRouter,Route} from 'react-router-dom'
var HomePageCSS= require('./homepage.css')
export default class HomePage extends React.Component{
  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        console.log('Received values of form: ', values);
      }
    });
  };
    render(){
      const { getFieldDecorator } = this.props.form
        return(
          <div className={HomePageCSS.login}>
             <Form onSubmit={this.handleSubmit} className="login-form">
        <Form.Item>
          {getFieldDecorator('username', {
            rules: [{ required: true, message: 'Please input your username!' }],
          })(
            <Input
              prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
              placeholder="Username"
            />,
          )}
        </Form.Item>
        <Form.Item>
          {getFieldDecorator('password', {
            rules: [{ required: true, message: 'Please input your Password!' }],
          })(
            <Input
              prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />}
              type="password"
              placeholder="Password"
            />,
          )}
        </Form.Item>
        <Form.Item>
          {getFieldDecorator('remember', {
            valuePropName: 'checked',
            initialValue: true,
          })(<Checkbox>Remember me</Checkbox>)}
          <a className="login-form-forgot" href="">
            Forgot password
          </a>
          <Button type="primary" htmlType="submit" className="login-form-button">
            Log in
          </Button>
          Or <a href="">register now!</a>
        </Form.Item>
      </Form>
            <DefaultHeader/>
          <div className={HomePageCSS.login1}>
          <div className={HomePageCSS.username}>
            <input placeholder="管理员账号" className={HomePageCSS.account}></input>
          </div>
          <div className={HomePageCSS.password}>
            <input placeholder="密码" className={HomePageCSS.word}></input>
          </div>
          <div className={HomePageCSS.button}> 
          <Link to="/menu/menulist"><button className={HomePageCSS.b}>登录</button></Link>
          </div>
          </div>
                
            </div>
        )
    }
}
